mapper.select:
select COALESCE(dwfl,'无'),COALESCE(dwflmc,'无') as name,sum(zbsl) as num from rpt.RPT_AQJG_JDWL_LCJG_SYDW where 1=1
and xzqh in #{item}
and dwflmc != '' group by 1,2

select COALESCE(dwdj,'无') as dwdj,COALESCE(dwdjmc,'未评定') as name,sum(zbsl) as num from rpt.RPT_AQJG_JDWL_LCJG_SYDW where dwflmc='末端网点'

    and xzqh in  #{item}


group by 1,2

select ssfxjmc as name,count(ssfxjmc) as num from rpt.rpt_aqjg_jdwl_dwjcxx where ssfxjmc!=''

    and  jcztdm =#{jcztdm}


    and  jclxdm =#{jclxdm}

 group by 1
