mapper.select:
with yhlxtj as (SELECT year||'-'||mon as date,year as yearDate,yhwwcsl,yhwcsl,yhzl FROM rpt_aqjg_ggjt_yhlxtj WHERE tjrq=to_char(CURRENT_DATE,'yyyy-mm-dd')) SELECT COALESCE(sum(yhzl),0) yhzl, COALESCE(sum(yhwcsl),0) yhwcsl, COALESCE(sum(yhwwcsl),0) yhwwcsl FROM yhlxtj WHERE
yearDate BETWEEN #{firstYear} AND #{twoYear}
date between #{month} and #{month2}

WITH dljtytgltj AS ( select * from ( select t.lx, t.year || '-' || t.mon as ny, sl FROM rpt_aqjg_ggjt_dljtytgltj t where t.tjrq = to_char(CURRENT_DATE -1, 'yyyy-MM-dd') and lx in ('1','2','3') ) dlj where 1=1
and ny BETWEEN #{nyold} || '-01' and #{nynew} || '-12'
and ny BETWEEN #{nyold} and #{nynew}
) select (select sum(sl) from dljtytgltj where lx = '1') as qysum, (select sum(sl) from dljtytgltj where lx = '2') as clsum, (select sum(sl) from dljtytgltj where lx = '3') as rysum

with yhlxtj as (SELECT year||'-'||mon as date,year as yearDate,ddbm,ddmc,yhwwcsl,yhwcsl,yhzl FROM rpt_aqjg_ggjt_yhlxtj WHERE tjrq=to_char(CURRENT_DATE,'yyyy-mm-dd')) SELECT ddmc, ddbm, sum(yhzl) yhzl, sum(yhwcsl) yhwcsl, sum(yhwwcsl) yhwwcsl FROM yhlxtj WHERE ddbm in('11','12','13','14','15') and
yearDate BETWEEN #{firstYear} AND #{twoYear}
date between #{month} and #{month2}
GROUP BY ddbm,ddmc ORDER BY ddmc

with yhlxtj as (SELECT year||'-'||mon as date,year as yearDate,ddbm,ddmc,yhwwcsl,yhwcsl,yhzl FROM rpt_aqjg_ggjt_yhlxtj WHERE tjrq=to_char(CURRENT_DATE,'yyyy-mm-dd')) SELECT ddmc, ddbm, sum(yhzl) yhzl, sum(yhwcsl) yhwcsl, sum(yhwwcsl) yhwwcsl FROM yhlxtj WHERE ddbm in('21','22','23','24','25') and
yearDate BETWEEN #{firstYear} AND #{twoYear}
date between #{month} and #{month2}
GROUP BY ddbm,ddmc ORDER BY ddmc

with yhlxtj as (SELECT year||'-'||mon as date,year as yearDate,ddbm,ddmc,yhwwcsl,yhwcsl,yhzl FROM rpt_aqjg_ggjt_yhlxtj WHERE tjrq=to_char(CURRENT_DATE,'yyyy-mm-dd')) SELECT ddmc, ddbm, sum(yhzl) yhzl, sum(yhwcsl) yhwcsl, sum(yhwwcsl) yhwwcsl FROM yhlxtj WHERE ddbm in('31','32','33','34','35','36','37','38','39') and
yearDate BETWEEN #{firstYear} AND #{twoYear}
date between #{month} and #{month2}
GROUP BY ddbm,ddmc ORDER BY ddmc

with yhlxtj as (SELECT year||'-'||mon as date,year as yearDate,jdlx,jdmc,zblx,zblxmc,sl FROM rpt_aqjg_ggjt_yhjdtj WHERE tjrq=to_char(CURRENT_DATE,'yyyy-mm-dd')) SELECT COALESCE(SUM(CASE WHEN jdlx='1' THEN sl ELSE 0 END),0) mjnum, COALESCE(SUM(CASE WHEN jdlx='2' AND zblx='21' THEN sl ELSE 0 END),0) zdlr, COALESCE(SUM(CASE WHEN jdlx='2' AND zblx='22' THEN sl ELSE 0 END),0) zdcl, COALESCE(SUM(CASE WHEN jdlx='2' AND zblx='23' THEN sl ELSE 0 END),0) zdja, COALESCE(SUM(CASE WHEN jdlx='1' AND zblx='24' THEN sl ELSE 0 END),0) zddcl, COALESCE(SUM(CASE WHEN jdlx='3' AND zblx='31' THEN sl ELSE 0 END),0) ddlr, COALESCE(SUM(CASE WHEN jdlx='3' AND zblx='32' THEN sl ELSE 0 END),0) ddcl, COALESCE(SUM(CASE WHEN jdlx='3' AND zblx='33' THEN sl ELSE 0 END),0) ddja, COALESCE(SUM(CASE WHEN jdlx='2' AND zblx='34' THEN sl ELSE 0 END),0) dddcl, COALESCE(SUM(CASE WHEN jdlx='4' AND zblx='41' THEN sl ELSE 0 END),0) ywlr, COALESCE(SUM(CASE WHEN jdlx='4' AND zblx='42' THEN sl ELSE 0 END),0) ywcl, COALESCE(SUM(CASE WHEN jdlx='4' AND zblx='43' THEN sl ELSE 0 END),0) ywja, COALESCE(SUM(CASE WHEN jdlx='4' AND zblx='44' THEN sl ELSE 0 END),0) ywdcl, COALESCE(SUM(CASE WHEN jdlx='5' AND zblx='51' THEN sl ELSE 0 END),0) zhidlr, COALESCE(SUM(CASE WHEN jdlx='5' AND zblx='52' THEN sl ELSE 0 END),0) zhidcl, COALESCE(SUM(CASE WHEN jdlx='5' AND zblx='53' THEN sl ELSE 0 END),0) zhidja, COALESCE(SUM(CASE WHEN jdlx='5' AND zblx='54' THEN sl ELSE 0 END),0) zhiddcl FROM yhlxtj where
yearDate between #{firstYear} AND #{twoYear}
date between #{month} and #{month2}

with yhlxtj as (SELECT year||'-'||mon as date,year as yearDate,yhlxmc,yhwcsl,yhlx,yhzl FROM rpt_aqjg_ggjt_yhlxtj WHERE tjrq=to_char(CURRENT_DATE,'yyyy-mm-dd')) SELECT yhlxmc, sum(yhwcsl) yhwcsl, sum(yhzl) yhzl FROM yhlxtj where
yearDate between #{firstYear} AND #{twoYear}
date between #{month} and #{month2}
GROUP BY yhlxmc,yhlx

WITH qyxzfx AS ( select * from ( select t.year || '-' || t.mon as ny, t.xzdl, t.xzdlmc, t.xzxl, coalesce(t.xzxlmc, t.xzdlmc) as xzxlmc, dwsl, xqdm, xqmc from rpt_aqjg_ggjt_qyxzfx t where t.tjrq = to_char(CURRENT_DATE -1, 'yyyy-MM-dd') and t.xzdl in ('1','2','3','4') ) dlj where 1=1
and ny BETWEEN #{nyold} || '-01' and #{nynew} || '-12'
and ny BETWEEN #{nyold} and #{nynew}
) select xzdl,xzdlmc,'flhj' xzxlmc, sum(dwsl) dwsl from qyxzfx group by xzdl,xzdlmc union all select xzdl,xzdlmc,xzxlmc,coalesce(sum(dwsl),0) dwsl from qyxzfx where xzdl='1' group by xzdlmc,xzxlmc,xzdl union all select xzdl,xzdlmc,xzxlmc,coalesce(sum(dwsl),0) dwsl from qyxzfx where xzdl='2' group by xzdlmc,xzxlmc,xzdl union all select xzdl,xzdlmc,xzxlmc,coalesce(sum(dwsl),0) dwsl from qyxzfx where xzdl='3' group by xzdlmc,xzxlmc,xzdl union all select xzdl,xzdlmc,xzxlmc,coalesce(sum(dwsl),0) dwsl from qyxzfx where xzdl='4' group by xzdlmc,xzxlmc,xzdl
